func MakeState(nVals, height int64) (sm.State, dbm.DB, map[string]types.PrivValidator) {
	vals := make([]types.GenesisValidator, nVals)
	privVals := make(map[string]types.PrivValidator, nVals)

	PrivValidatorKeyFile := "/home/nampkh/.tendermint/config/priv_validator_key.json"
	PrivValidatorStateFile := "/home/nampkh/.tendermint/data/priv_validator_state.json"
	filePV := privval.LoadOrGenFilePV(PrivValidatorKeyFile, PrivValidatorStateFile)

	for i := int64(0); i < nVals; i++ {
		pubkey, _ := filePV.GetPubKey()

		vals[i] = types.GenesisValidator{
			Address: filePV.GetAddress(),
			PubKey:  pubkey,
			Power:   100,
			Name:    fmt.Sprintf("test%d", i),
		}
		privVals[filePV.GetAddress().String()] = types.NewMockPVWithParams(filePV.Key.PrivKey, false, false)
	}
	s, _ := sm.MakeGenesisState(&types.GenesisDoc{
		ChainID:    chainID,
		Validators: vals,
		AppHash:    nil,
	})

	stateDB := dbm.NewMemDB()
	stateStore := sm.NewStore(stateDB)
	if err := stateStore.Save(s); err != nil {
		panic(err)
	}

	for i := int64(1); i < height; i++ {
		s.LastBlockHeight++
		s.LastValidators = s.Validators.Copy()
		if err := stateStore.Save(s); err != nil {
			panic(err)
		}
	}

	return s, stateDB, privVals
}

func MakeBlock(state sm.State, height int64) *types.Block {
	block, _ := state.MakeBlock(
		height,
		MakeTxs(state.LastBlockHeight),
		new(types.Commit),
		[]types.Evidence{},
		state.Validators.GetProposer().Address,
	)
	return block
}

// make some bogus txs
func MakeTxs(height int64) (txs []types.Tx) {
	for i := 0; i < nTxsPerBlock; i++ {
		txs = append(txs, types.Tx([]byte{byte(height), byte(i)}))
	}
	return txs
}
